    /** Calls external command
     * @param commandLine
     * @return Result */
    public String externalCall(String commandLine){

        String resultValue = "";
        try {

            // create Runtime environment and call
            Runtime r = Runtime.getRuntime();
            Process p = r.exec(commandLine);
            p.waitFor();

            // log output return values
            BufferedReader b = new BufferedReader(new InputStreamReader(p.getInputStream()));
            String line = "";
            while ((line = b.readLine()) != null) {
                resultValue = resultValue + line;
            }
            b.close();

        } catch (Exception e){
            logger.error("Exception while executing command: " + e.getMessage());
        }
        return resultValue;

    }

    /** Creating 10 million files (one for each image entry) with 10k columns (concepts) */
    public void storageApproach1(String folder){

        // take time
        Date start = new Date();

        // create directory
        boolean success = (new File(folder)).mkdirs();
        if (!success) {
            logger.error("Could not create folder: ");
        }

        // create 10.000 random numbers
        String valueCSV = "";
        for (int j = 0; j < 10000; j++){

            // create random number
            int value = randomInt(0,1);

            // add to CSV string
            valueCSV = valueCSV + ", " + value;

        }

        // create files
        for (int i = 1; i < 10000000; i++){

            // define fileName
            String fileName = new String(folder + "/" + i + ".txt");

            // create file and insert 10k concept probabilities
            try {
                PrintWriter writer = new PrintWriter(fileName, "UTF-8");
                writer.println(i + valueCSV);
                writer.close();
            } catch (Exception e){
                logger.error("Error while writing file: " + i);
            }

            if (i%100000 == 0){
                logger.info("# of files: " + i);
            }

        }

        // display time
        Date end = new Date();
        logger.debug("StorageApproach 1: Indexing time: " + (end.getTime() - start.getTime()) + " total milliseconds");
    }

